Action.Str:
var response = JSON.parse(global( 'HTTPD') );
var sunrise = response.moon_phase.sunrise;
var sunset = response.moon_phase.sunset;
var sunriseString = sunrise.hour <10 ? "0" : "";
sunriseString += sunrise.hour.toString() + "." + sunrise.minute.toString();
flash(sunriseString);
var sunsetString = sunset.hour < 10 ? "0" : "";
sunsetString += sunset.hour.toString() + "." + sunset.minute.toString()
flash(sunsetString);
setGlobal( 'SUNRISE', sunriseString);
setGlobal( 'SUNSET', sunsetString );
